import DashboardApp from "./components/DashboardApp";
import Sidebar from "./components/Sidebar";
import { BrowserRouter as Router, Switch, Route ,Redirect} from 'react-router-dom';
import LoginMA from "./components/LoginMA";
function App() {
  const jwt = localStorage.getItem('jwt');

  if(!jwt) {
    return <LoginMA />
  }
  return (

    <>
      <div className="dashboard">
      <Router>
      <Sidebar />

      <Switch>


      <Route path='/logina' component={LoginMA} />  
      <Route path='/dash' component={DashboardApp} /> 

        </Switch>
        </Router>
      </div>
      <div className="shapes__one"></div>
      <div className="shapes__two"></div>
      <div className="shapes__three"></div>
    </>
  );
}

export default App;
