import { SiGoogleanalytics } from "react-icons/si";
import { BiNews, BiRocket } from "react-icons/bi";
import { FaWallet } from "react-icons/fa";
import { AiFillSetting, AiFillAppstore } from "react-icons/ai";
import { BsCashStack } from "react-icons/bs";
import { MdAccountCircle } from "react-icons/md";
import React, { useState, useEffect } from "react";
import styled from "styled-components";
import { MdSpeakerGroup ,MdDashboard,MdCardMembership, MdChat} from "react-icons/md";
import scrollreveal from "scrollreveal";
import { FiLogOut } from "react-icons/fi";
import { IoSettings } from "react-icons/io5";

import { FaAddressCard, FaTaxi,FaIdCard,FaNewspaper } from "react-icons/fa";
import { NavLink as Link, NavLink } from 'react-router-dom';
export default function Sidebar() {
  const user = JSON.parse(localStorage.getItem('user'));
  const jwt =JSON.parse(localStorage.getItem('jwt'));

  console.log(jwt)
  const handleLogout = () => {
    localStorage.removeItem("jwt");
    localStorage.removeItem("user");

    localStorage.removeItem("amount");
    window.location.href = "/login";}
  const [currentLink, setCurrentLink] = useState(1);
  const [navbarState, setNavbarState] = useState(false);
  const html = document.querySelector("html");
  html.addEventListener("click", () => setNavbarState(false));

  useEffect(() => {
    const sr = scrollreveal({
      origin: "left",
      distance: "80px",
      duration: 1000,
      reset: false,
    });

    sr.reveal(
      `
          .brand,
          .links>ul>li:nth-of-type(1),
      .links>ul>li:nth-of-type(2),
      .links>ul>li:nth-of-type(3),
      .links>ul>li:nth-of-type(4),
      .links>ul>li:nth-of-type(5),
      .links>ul>li:nth-of-type(6),
      .logout
      `,
      {
        opacity: 0,
        interval: 300,
      }
    );
  }, []);

  return (
    <> <Section>
    <div className="top">
      <div className="brand">

        <FaIdCard />
        <span>Bazra Payment</span>
      </div>
      <div className="toggle">
        {navbarState ? (
          <MdSpeakerGroup onClick={() => setNavbarState(false)} />
        ) : (
          <MdSpeakerGroup
            onClick={(e) => {
              e.stopPropagation();
              setNavbarState(true);
            }}
          />
        )}
      </div>
      <div className="links">
        <ul>
          <li
            className={currentLink === 1 ? "active" : "none"}
            onClick={() => setCurrentLink(1)}
          > <NavLink  to='/login' style={{ textDecoration: 'none' }} activeStyle>
            <a href="#">



              <MdDashboard />
              <span> Dashboard</span>

            </a>
            </NavLink >
          </li>
          <li
            className={currentLink === 2 ? "active" : "none"}
            onClick={() => setCurrentLink(2)}
          ><NavLink to='/about' style={{ textDecoration: 'none' }} activeStyle>
            <a href="#">
              <MdCardMembership />
              <span> Profile</span>
            </a>
            </NavLink>
          </li>
          <li
            className={currentLink === 3 ? "active" : "none"}
            onClick={() => setCurrentLink(3)}
          >
            <a href="#">
              <FaAddressCard/>
              <span> Payment Details</span>
            </a>
          </li>
          <li
            className={currentLink === 4 ? "active" : "none"}
            onClick={() => setCurrentLink(4)}
          >
            <a href="#">
              <MdChat/>
              <span>Chat</span>
            </a>
          </li>
          <li
            className={currentLink === 5 ? "active" : "none"}
            onClick={() => setCurrentLink(5)}
          >
            <a href="#">
              <MdSpeakerGroup/>
              <span> FAQs</span>
            </a>
          </li>
          <li
            className={currentLink === 6 ? "active" : "none"}
            onClick={() => setCurrentLink(6)}
          >
            <a href="#">
              <IoSettings/>
              <span> Settings</span>
            </a>
          </li>
        </ul>
      </div>
      <div className="logout">
      <a href="#">
        <FiLogOut />
        <button className="logout" onClick={handleLogout}>Logout</button>
      </a>
    </div>
    </div>

  </Section>
  <ResponsiveNav state={navbarState} className={navbarState ? "show" : ""}>
    <div className="responsive__links">
      <ul>
        <li
          className={currentLink === 1 ? "active" : "none"}
          onClick={() => setCurrentLink(1)}
        >
          <a href="#">
            <MdDashboard />
            <span> Dashboard</span>
          </a>
        </li>
        <li
          className={currentLink === 2 ? "active" : "none"}
          onClick={() => setCurrentLink(2)}
        >
          <a href="#">
            <FaNewspaper />
            <span>Profile</span>
          </a>
        </li>
        <li
          className={currentLink === 3 ? "active" : "none"}
          onClick={() => setCurrentLink(3)}
        >
          <a href="#">
            < FaAddressCard />
            <span> Payment Details</span>
          </a>
        </li>
        <li
          className={currentLink === 4 ? "active" : "none"}
          onClick={() => setCurrentLink(4)}
        >
          <a href="#">
            <MdChat />
            <span> Chat</span>
          </a>
        </li>
        <li
          className={currentLink === 5 ? "active" : "none"}
          onClick={() => setCurrentLink(5)}
        >
          <a href="#">
            <MdSpeakerGroup />
            <span> FAQs</span>
          </a>
        </li>
        <li
          className={currentLink === 6 ? "active" : "none"}
          onClick={() => setCurrentLink(6)}
        >
          <a href="#">
            <IoSettings />
            <span> Settings</span>
          </a>
        </li>
      </ul>
    </div>
  </ResponsiveNav>
</>
);
}
const Section = styled.section;

const ResponsiveNav = styled.div
